#include <stdint.h>
#include "main.h"

#if !defined(__SOFT_FP__) && defined(__ARM_FP)
  #warning "FPU is not initialized, but the project is compiling for an FPU. Please initialize the FPU before use."
#endif


int main(void)
{
	RCC_AHB1ENR_t volatile *const pAhb1CLK = (RCC_AHB1ENR_t*)0x40023830;
	GPIOx_MODER_t volatile *const pGpioaMode = (GPIOx_MODER_t*)0x40020000;
	GPIOx_MODER_t volatile *const pGpiodMode = (GPIOx_MODER_t*)0x40020C00;
	GPIOx_IDR_t const volatile *const pGpioaInput = (GPIOx_IDR_t*)0x40020010;
	GPIOx_ODR_t	volatile *const pGpiodOutput = (GPIOx_ODR_t*)0x40020C14;

	// Enable clocks
	pAhb1CLK->gpioaen = 0x1;
	pAhb1CLK->gpioden = 0x1;

	// Set Mode
	pGpioaMode->moder0 = 0x00;
	pGpiodMode->moder12 = 0x01;

	// Toggle PD12
	while(1){
		// Read PA0
		uint8_t pinStatus = pGpioaInput->idr0 & 0x1;
		if (pinStatus == 0x1){
			pGpiodOutput->odr12 = 0x1; // Turn on LED
		}
		else pGpiodOutput->odr12 = 0x0; // Turn off LED

	}

	/* Loop forever */
	for(;;);
}
