#include <stdint.h>

#if !defined(__SOFT_FP__) && defined(__ARM_FP)
  #warning "FPU is not initialized, but the project is compiling for an FPU. Please initialize the FPU before use."
#endif

#define RCC_BASE_ADDR 			0x40023800UL
#define RCC_CFGR_REG_OFFSET 	0x08UL
#define RCC_CR_REG_OFFSET		0x00UL


#define RCC_CFGR_REG_ADDR 		(RCC_BASE_ADDR + RCC_CFGR_REG_OFFSET)
#define RCC_CR_REG_ADDR 		(RCC_BASE_ADDR + RCC_CR_REG_OFFSET)

#define GPIOA_BASE_ADDR 		0x40020000UL

int main(void)
{
	uint32_t *pRccCrReg = (uint32_t*)RCC_CR_REG_ADDR;
	uint32_t *pRccCfgrReg = (uint32_t*)RCC_CFGR_REG_ADDR;

    //1. Enable the HSE clock using HSEON bit (RCC_CR)
	*pRccCrReg |= (1 << 16);

	//2. Wait until HSE Clock from the external crystal stabilizes (only if crystal is stable: HSE clock ready flag)
	while ( !(*pRccCrReg & (1<<17)) ); // Hang program until crystal stable

	//3. Switch the system clock to HSE (RCC_CFGR)
	*pRccCfgrReg |= (1 << 0);

	/******** Do MC01 settings to measure it ********/

	//1. Configure RCC_CFGR MC01 bit fields to select HSE as clock source
	*pRccCfgrReg &= ~(1 << 21); // Clear 21st
	*pRccCfgrReg |= (1 << 22); //Set 22nd

	//Configure MC01 prescaler (divide HSE by 4)
	*pRccCfgrReg |= (3 << 25);

	//2. Configure PA8 to AF0 mode to behave as MC01 signal

	//a) enable peripheral clock for GPIOA peripheral
	uint32_t *pRCCAhb1Enr = (uint32_t*)(RCC_BASE_ADDR + 0x30);
	*pRCCAhb1Enr |= (1<< 0); //Enable GPIOA peripheral clock

	//b) configure mode of GPIOA pin as alternate function mode
	uint32_t *pGPIOAModeReg = (uint32_t*)(GPIOA_BASE_ADDR + 0x00);
	*pGPIOAModeReg &= ~(0x3 << 16); //clear
	*pGPIOAModeReg |= (0x2 << 16); //set

	//c) configure the alternate function register to set the mode 0 for PA8
	uint32_t *pGPIOAAltFunHighReg = (uint32_t*)(GPIOA_BASE_ADDR + 0x24);
	*pGPIOAAltFunHighReg &= ~(0xF <<0);
	/* Loop forever */
	for(;;);
}
